/*!50003 DROP PROCEDURE IF EXISTS  `pproductosmovint` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `pproductosmovint`(IN filtro VARCHAR(25))
BEGIN
	SELECT p.prodCodigo AS Código, 
	p.prodDescripcion AS Descripción, 
	CASE
		WHEN  SUM(e.exiscantidad) < 0 THEN 0
		ELSE SUM(e.exiscantidad)
		
		
	END AS `Stock Disponible`
	FROM stproductos p
	INNER JOIN stexistencia e
	ON p.prodCodigo = e.codigo_base AND (e.`existEstado` <> 'Anulado' OR e.`existEstado` IS NULL)  WHERE (CAST(p.prodCodigo AS CHAR) LIKE CONCAT('%', filtro, '%') OR CAST(p.prodDescripcion AS CHAR) LIKE CONCAT('%', filtro, '%') ) 
	GROUP BY e.codigo_base;
END */$$
